import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import NavBar from "./components/navbar";
import Counters from "./components/counters";
import jumbotronHome from "./components/jumbotronhome";
import homePhoto from "./assets/homephoto2.png";
import { ButtonToolbar } from "react-bootstrap";
class App extends Component {
  state = {
    counters: [
      { id: 1, value: 0 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
      { id: 5, value: 0 },
    ],
  };

  handleDelete = (counterId) => {
    const counters = this.state.counters.filter((c) => c.id !== counterId);
    this.setState({ counters });
  };

  handleReset = () => {
    const counters = this.state.counters.map((c) => {
      c.value = 0;
      return c;
    });
    this.setState({ counters });
  };

  handleIncrement = (counter) => {
    const counters = [...this.state.counters];
    const index = counters.indexOf(counter);

    counters[index] = { ...counter };
    counters[index].value++;
    this.setState({ counters: counters });
  };

  render() {
    return (
      <React.Fragment>
        <NavBar />
        {/* <jumbotronHome /> */}
        <div
          style={{
            backgroundImage: `url(https://images.unsplash.com/photo-1480365501497-199581be0e66?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=750&q=80)`,
          }}
          className="jumbotron jumbotron-fluid"
          className="text-right py-5 px-4"
        >
          <div className="container">
            <h2 className="display-4" className="text-right py-5 px-4 my-5">
              Photography made simple
            </h2>
            <p className="lead medium" className="text-right py-5  my-5">
              Need a talented individual to cover an occasion? Browse through
              our list of curated photographers or let us find one for you
            </p>
            <a a className="btn btn-outline-light ml-2" href="#">
              Book Now
            </a>
            <a a className=" btn btn-outline-light ml-2" href="#">
              Recommendation
            </a>
          </div>
        </div>
        {/* <main className="container mt-5 ">
          <Counters
            onReset={this.handleReset}
            onIncrement={this.handleIncrement}
            onDelete={this.handleDelete}
            counters={this.state.counters}
          />
        </main> */}
      </React.Fragment>
    );
  }
}

export default App;
