import React, { Component } from "react";
import ToggleButton from "react-bootstrap/ToggleButton";
import ToggleButtonGroup from "react-bootstrap/ToggleButtonGroup";
import "react-bootstrap-range-slider/dist/react-bootstrap-range-slider.css";
import RangeSlider from "react-bootstrap-range-slider";
import Slider from "react-rangeslider";
import { Range } from "framework7-react";

// To include the default styles
import "react-rangeslider/lib/index.css";
class Filter extends Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      volume: 0,
    };
  }
  handleOnChange = (value) => {
    this.setState({
      volume: value,
      priceMin: 1000,
      priceMax: 10000,
    });
  };
  render() {
    let { volume } = this.state;
    return (
      <React.Fragment>
        <Range
          min={0}
          max={500}
          step={1}
          value={[this.state.priceMin, this.state.priceMax]}
          label={true}
          dual={true}
          color="green"
          onRangeChange={this.onPriceChange.bind(this)}
        ></Range>
        <RangeSlider
          value={this.state.value}
          onChange={this.props.handleRangeChange}
        />
        <Slider
          value={volume}
          orientation="horizontal"
          step={1000}
          tooltip={true}
          max={100000}
          onChange={this.handleOnChange}
        />
        <ToggleButtonGroup
          type="radio"
          name="options"
          defaultValue={1}
          onClick={this.props.handleClick}
        >
          <ToggleButton value={"All"}>All</ToggleButton>
          <ToggleButton value={"Silver"}>Silver</ToggleButton>
          <ToggleButton value={"Gold"}>Gold</ToggleButton>
          <ToggleButton value={"Platinum"}>Platinum</ToggleButton>
        </ToggleButtonGroup>
      </React.Fragment>
    );
  }
}

export default Filter;
