import React, { Component } from "react";

class Component1 extends Component {
  state = {
    value: this.props.value,
    tags: ["tag1", "tag2", "tag3", "tag4"],
    url:
      "https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.pexels.com%2Fphoto%2Fblack-dslr-camera-with-black-background-1251299%2F&psig=AOvVaw3SRlztYJ5w1N86sNwXGuPe&ust=1586347134137000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCKD10N2h1ugCFQAAAAAdAAAAABAD",
  };

  styles = {
    fontSize: 50,
    fontWeight: 10,
  };

  increment = (product) => {
    console.log(product);
    this.setState({ value: this.state.value + 1 });
  };

  render() {
    return (
      <React.Fragment>
        <span className={this.getClass()}>{this.func()}</span>
        <button
          onClick={() => this.increment({ id: 1 })}
          className="btn btn-secondary btn-sm"
        >
          {" "}
          increment
        </button>
        <ul>
          {this.state.tags.map((tag) => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </React.Fragment>
    );
  }
  getClass() {
    let classes = "badge m-2 badge-";
    return (classes += this.state.value === 0 ? "primary" : "warning");
    return classes;
  }

  func() {
    return this.state.value;
  }
}

export default Component1;
