import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import ToggleButton from "react-bootstrap/ToggleButton";
import bg from "../assets/wedding_cover.jpg";
import Recphoto from "../assets/Recommend.jpg";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import PhotographersCard from "./photographersCard";

class PhotographerSelection extends Component {
  constructor(props) {
    // constructor
    super(props);
    this.state = {
      photographers: [
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
        {
          key: "",
          name: "Shahmeer Ahmad Khan",
          level: "Platinum",
          price: 25000,
        },
      ],
    };

    this.jumbotronCode = this.jumbotronCode.bind(this);
    this.PhotographersCards = this.PhotographersCards.bind(this);
  }

  jumbotronCode() {
    return (
      <div
        style={{
          backgroundImage: "url(" + Recphoto + ")",
          paddingTop: 50,
          padding: 50,
          paddingBottom: 50,
          backgroundPosition: "center",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          width: "100vw",
          height: "70vh",
        }}
        className="jumbotron jumbotron-fluid"
        className="text-right p-5"
      >
        <div className="container">
          <h1
            className="display-4"
            className="text-center text-white pr-auto pt-auto pb-auto mt-auto"
          >
            Wedding
          </h1>
          <p
            className="lead medium"
            className="text-center text-white pr-auto mt-auto"
          >
            Fill the form below and we will get back to you with a<br />
            photographer that best suits your needs. You'll have a<br />
            photographer in less than 24 hours!
            <br />
          </p>
        </div>
      </div>
    );
  }
  componentDidMount() {
    //function runs at the start of component loading
    axios //sending a get request to get all the photographers of the category from Mongo
      .get("http://localhost:5000/photographers/")
      .then((res) => {
        res.data.map((entree) =>
          this.setState({
            categories: this.state.photographers.concat([
              //storing all the category ids and their name values in my local state array
              { key: entree._id, name: entree.categoryname },
            ]),
          })
        );
        // console.log(this.state.categories);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    let cards = this.state.photographers.map((Photographer) => {
      return (
        <Col lg="auto" sm="auto" md="auto">
          <PhotographersCard Photographer={Photographer} />
        </Col>
      );
    });
    return (
      <React.Fragment>
        {/* <Col md={{ span: 4, offset: 4 }}>{`md={{ span: 4, offset: 4 }}`}</Col> */}
        {this.jumbotronCode()}
        <Container fluid>
          <Row lg="auto" sm="auto" md="auto">
            {cards}
          </Row>
        </Container>
      </React.Fragment>
    );
  }
}

export default PhotographerSelection;
