import React from "react";
import styled from "styled-components";
import Recphoto from "../assets/home2.jpg";

class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      age: null,
      contact: null,
      email: "",
      location: "",
      date: "",
      description: "",
    };
  }
  mySubmitHandler = (event) => {
    event.preventDefault();
    let age = this.state.age;
    if (!Number(age)) {
      alert("Your age must be a number");
    }
  };
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  };
  render() {
    return (
      <React.Fragment>
        //jumborton board and image
        <div
          style={{
            backgroundImage: "url(" + Recphoto + ")",
            paddingTop: 50,
            padding: 50,
            paddingBottom: 50,
            backgroundPosition: "center",
            backgroundSize: "cover",
            backgroundRepeat: "no-repeat",
            width: "100vw",
            height: "100vh",
          }}
          className="jumbotron jumbotron-fluid"
          className="text-right p-5"
        >
          <div className="container">
            <h2
              className="display-4"
              className="text-left text-white pr-5 pt-5 mt-5"
            >
              Our Vision
            </h2>
            <p
              className="lead medium"
              className="text-left text-white pr-5 mt-1"
            >
              Need a talented individual to cover an <br />
              occasion? Browse through our list of curated <br />
              photographers or let us find one for you
            </p>
          </div>
        </div>
        //Form for getting info starts here
        <form onSubmit={this.mySubmitHandler}>
          <h3>
            Hello {this.state.username} <br /> please enter your info below
          </h3>
          <p>Full Name</p>
          <input type="text" name="username" onChange={this.myChangeHandler} />
          <p>Contact Number</p>
          <input type="text" name="contact" onChange={this.myChangeHandler} />
          <p>Email</p>
          <input type="text" name="email" onChange={this.myChangeHandler} />
          <p>Location</p>
          <input type="text" name="location" onChange={this.myChangeHandler} />
          <p>Date</p>
          <input type="text" name="date" onChange={this.myChangeHandler} />
          <p>Event description</p>
          <input
            type="text"
            name="description"
            onChange={this.myChangeHandler}
          />
          <br />
          <br />
          <input type="submit" />
        </form>
      </React.Fragment>
    );
  }
}

export default MyForm;
